import { useEffect, useState } from "react";
import { FaSchool, FaUserTie, FaBus, FaMapMarkerAlt } from "react-icons/fa"; // icons
import API from "../../services/api";

function Dashboard() {
  const [counts, setCounts] = useState({
    schools: 0,
    admins: 0,
    drivers: 0,
    stops: 0,
  });

  useEffect(() => {
    fetchCounts();
  }, []);

  const fetchCounts = async () => {
    try {
      const schoolsRes = await API.get("/schools");
      const adminsRes = await API.get("/schooladmins");
      const stopsRes = await API.get("/stops/all");

      setCounts({
        schools: schoolsRes.data.length,
        admins: adminsRes.data.length,
        drivers: 0, // ⚠️ needs backend endpoint for all drivers
        stops: stopsRes.data.length,
      });
    } catch (err) {
      console.error("Error fetching counts:", err);
    }
  };

  return (
    <div className="p-6">
      <h1 className="text-3xl font-bold mb-8">Superadmin Dashboard</h1>

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        {/* Schools */}
        <div className="bg-white shadow-md rounded-lg p-6 flex items-center gap-4">
          <div className="p-4 bg-blue-100 rounded-full">
            <FaSchool className="text-blue-600 text-3xl" />
          </div>
          <div>
            <p className="text-gray-500 text-sm">Total Schools</p>
            <h2 className="text-2xl font-bold">{counts.schools}</h2>
          </div>
        </div>

        {/* School Admins */}
        <div className="bg-white shadow-md rounded-lg p-6 flex items-center gap-4">
          <div className="p-4 bg-green-100 rounded-full">
            <FaUserTie className="text-green-600 text-3xl" />
          </div>
          <div>
            <p className="text-gray-500 text-sm">School Admins</p>
            <h2 className="text-2xl font-bold">{counts.admins}</h2>
          </div>
        </div>

        {/* Drivers */}
        <div className="bg-white shadow-md rounded-lg p-6 flex items-center gap-4">
          <div className="p-4 bg-yellow-100 rounded-full">
            <FaBus className="text-yellow-600 text-3xl" />
          </div>
          <div>
            <p className="text-gray-500 text-sm">Drivers</p>
            <h2 className="text-2xl font-bold">{counts.drivers}</h2>
          </div>
        </div>

        {/* Stops */}
        <div className="bg-white shadow-md rounded-lg p-6 flex items-center gap-4">
          <div className="p-4 bg-red-100 rounded-full">
            <FaMapMarkerAlt className="text-red-600 text-3xl" />
          </div>
          <div>
            <p className="text-gray-500 text-sm">Stops</p>
            <h2 className="text-2xl font-bold">{counts.stops}</h2>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Dashboard;
