import { useEffect, useState } from "react";
import { FaSchool, FaUserTie, FaBus, FaMapMarkerAlt } from "react-icons/fa";
import {
  LineChart,
  Line,
  CartesianGrid,
  XAxis,
  YAxis,
  Tooltip,
  BarChart,
  Bar,
  Legend,
} from "recharts";
import API from "../../services/api";

function Dashboard() {
  const [counts, setCounts] = useState({
    schools: 0,
    admins: 0,
    drivers: 0,
    stops: 0,
  });

  const [driverData, setDriverData] = useState([]);
  const [weeklyData, setWeeklyData] = useState([]);
  const [drivers, setDrivers] = useState([]);
  const [schools, setSchools] = useState([]);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const schoolsRes = await API.get("/schools");
      const adminsRes = await API.get("/schooladmins");
      const stopsRes = await API.get("/stops/all");
      const driversRes = await API.get("/drivers/all"); // superadmin only

      setCounts({
        schools: schoolsRes.data.length,
        admins: adminsRes.data.length,
        drivers: driversRes.data.length,
        stops: stopsRes.data.length,
      });

      // populate tables
      setDrivers(driversRes.data);
      setSchools(schoolsRes.data);

      // Example: driver stats by month from stops timestamps
      const monthly = {};
      stopsRes.data.forEach((st) => {
        const m = new Date(st.created_at).toLocaleString("default", { month: "short" });
        monthly[m] = (monthly[m] || 0) + 1;
      });
      setDriverData(Object.keys(monthly).map((m) => ({ month: m, drivers: monthly[m] })));

      // Example: weekly changes (for now: based on stops count per driver)
      const weekly = [
        { name: "Routes", Added: 0, Modified: 0, Deleted: 0 },
        { name: "Drivers", Added: driversRes.data.length, Modified: 0, Deleted: 0 },
        { name: "Stops", Added: stopsRes.data.length, Modified: 0, Deleted: 0 },
      ];
      setWeeklyData(weekly);
    } catch (err) {
      console.error("Error fetching dashboard data:", err);
    }
  };

  return (
    <div className="p-6">
      <h1 className="text-3xl font-bold mb-8">Admin Dashboard</h1>

      {/* Top Stats */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-10">
        <div className="bg-white shadow rounded-lg p-6 flex items-center gap-4">
          <FaBus className="text-blue-600 text-3xl" />
          <div>
            <p className="text-gray-500 text-sm">Number of Drivers</p>
            <h2 className="text-2xl font-bold">{counts.drivers}</h2>
          </div>
        </div>
        <div className="bg-white shadow rounded-lg p-6 flex items-center gap-4">
          <FaMapMarkerAlt className="text-purple-600 text-3xl" />
          <div>
            <p className="text-gray-500 text-sm">Total Routes</p>
            <h2 className="text-2xl font-bold">-</h2> {/* add when backend ready */}
          </div>
        </div>
        <div className="bg-white shadow rounded-lg p-6 flex items-center gap-4">
          <FaSchool className="text-indigo-600 text-3xl" />
          <div>
            <p className="text-gray-500 text-sm">Total Stops</p>
            <h2 className="text-2xl font-bold">{counts.stops}</h2>
          </div>
        </div>
        <div className="bg-white shadow rounded-lg p-6 flex items-center gap-4">
          <FaUserTie className="text-green-600 text-3xl" />
          <div>
            <p className="text-gray-500 text-sm">Total Users</p>
            <h2 className="text-2xl font-bold">{counts.admins}</h2>
          </div>
        </div>
      </div>

      {/* Charts */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6 mb-10">
        {/* Line Chart */}
        <div className="bg-white shadow rounded-lg p-6">
          <h3 className="text-lg font-semibold mb-4">This Year</h3>
          <LineChart width={400} height={250} data={driverData}>
            <Line type="monotone" dataKey="drivers" stroke="#2563eb" strokeWidth={2} />
            <CartesianGrid stroke="#ccc" strokeDasharray="5 5" />
            <XAxis dataKey="month" />
            <YAxis />
            <Tooltip />
          </LineChart>
        </div>

        {/* Bar Chart */}
        <div className="bg-white shadow rounded-lg p-6">
          <h3 className="text-lg font-semibold mb-4">Weekly Updates</h3>
          <BarChart width={400} height={250} data={weeklyData}>
            <CartesianGrid strokeDasharray="3 3" />
            <XAxis dataKey="name" />
            <YAxis />
            <Tooltip />
            <Legend />
            <Bar dataKey="Added" fill="#3b82f6" />
            <Bar dataKey="Modified" fill="#f59e0b" />
            <Bar dataKey="Deleted" fill="#ef4444" />
          </BarChart>
        </div>
      </div>

      {/* Bottom Tables */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Driver Details */}
        <div className="bg-white shadow rounded-lg p-6">
          <h3 className="text-lg font-semibold mb-4">Driver Details</h3>
          <table className="w-full text-sm border">
            <thead>
              <tr className="bg-gray-100">
                <th className="p-2 border">Name</th>
                <th className="p-2 border">Username</th>
                <th className="p-2 border">School</th>
              </tr>
            </thead>
            <tbody>
              {drivers.map((d) => (
                <tr key={d.id}>
                  <td className="p-2 border">{d.name}</td>
                  <td className="p-2 border">{d.username}</td>
                  <td className="p-2 border">{d.school_name}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>

        {/* Institutes */}
