// src/pages/ManageStops.js
import { useEffect, useState } from "react";
import axios from "axios";

export default function ManageStops() {
  const [drivers, setDrivers] = useState([]);
  const [stops, setStops] = useState([]);
  const [selectedDriver, setSelectedDriver] = useState(null);

  const role = localStorage.getItem("role");
  const token = localStorage.getItem("token");
  const headers = { Authorization: `Bearer ${token}` };

  // eslint-disable-next-line react-hooks/exhaustive-deps

  // ‚úÖ Fetch drivers on load
  useEffect(() => {
    fetchDrivers();
  }, []);

  const fetchDrivers = async () => {
    try {
      const url =
        role === "superadmin"
          ? "https://gps.smartbus360.com/api/drivers/all"
          : "https://gps.smartbus360.com/api/drivers";

      const res = await axios.get(url, { headers });
      setDrivers(res.data);
    } catch (err) {
      console.error("Error fetching drivers:", err);
    }
  };

  // ‚úÖ Fetch stops by driver
  const fetchStopsByDriver = async (driverId) => {
    try {
      const url = `https://gps.smartbus360.com/api/drivers/by-driver/${driverId}`;
      const res = await axios.get(url, { headers });
      setStops(res.data);
      setSelectedDriver(driverId);
    } catch (err) {
      console.error("Error fetching stops:", err);
    }
  };

  return (
    <div>
      <h1 className="text-2xl font-bold mb-6">üõë Manage Stops</h1>

      {/* Step 1: Drivers list */}
      {!selectedDriver && (
        <table className="min-w-full bg-white border rounded shadow mb-6">
          <thead>
            <tr className="bg-gray-200 text-left">
              <th className="py-2 px-4 border">ID</th>
              <th className="py-2 px-4 border">Name</th>
              <th className="py-2 px-4 border">Username</th>
              {role === "superadmin" && (
                <th className="py-2 px-4 border">School</th>
              )}
              <th className="py-2 px-4 border">Action</th>
            </tr>
          </thead>
          <tbody>
            {drivers.map((driver) => (
              <tr key={driver.id}>
                <td className="py-2 px-4 border">{driver.id}</td>
                <td className="py-2 px-4 border">{driver.name}</td>
                <td className="py-2 px-4 border">{driver.username}</td>
                {role === "superadmin" && (
                  <td className="py-2 px-4 border">{driver.school_name}</td>
                )}
                <td className="py-2 px-4 border">
                  <button
                    onClick={() => fetchStopsByDriver(driver.id)}
                    className="bg-blue-600 text-white px-3 py-1 rounded"
                  >
                    View Stops
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}

      {/* Step 2: Stops for selected driver */}
      {selectedDriver && (
        <div>
          <button
            onClick={() => {
              setSelectedDriver(null);
              setStops([]);
            }}
            className="mb-4 bg-gray-600 text-white px-4 py-2 rounded"
          >
            ‚Üê Back to Drivers
          </button>

          {stops.length === 0 ? (
            <p>No stops found for this driver.</p>
          ) : (
            <table className="min-w-full bg-white border rounded shadow">
              <thead>
                <tr className="bg-gray-200 text-left">
                  <th className="py-2 px-4 border">ID</th>
                  <th className="py-2 px-4 border">Round</th>
                  <th className="py-2 px-4 border">Stop Order</th>
                  <th className="py-2 px-4 border">Latitude</th>
                  <th className="py-2 px-4 border">Longitude</th>
                  <th className="py-2 px-4 border">Placename</th>
                  <th className="py-2 px-4 border">Created At</th>
                </tr>
              </thead>
              <tbody>
                {stops.map((stop) => (
                  <tr key={stop.id}>
                    <td className="py-2 px-4 border">{stop.id}</td>
                    <td className="py-2 px-4 border">{stop.round_name}</td>
                    <td className="py-2 px-4 border">{stop.stop_order}</td>
                    <td className="py-2 px-4 border">{stop.latitude}</td>
                    <td className="py-2 px-4 border">{stop.longitude}</td>
                    <td className="py-2 px-4 border">{stop.placename}</td>
                    <td className="py-2 px-4 border">
                      {new Date(stop.created_at).toLocaleString()}
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          )}
        </div>
      )}
    </div>
  );
}
