// src/pages/SchoolAdmin/Dashboard.jsx
import { useEffect, useState } from "react";
import API from "../../services/api";

function SchoolDashboard() {
  const [counts, setCounts] = useState({ drivers: 0, stops: 0 });

  useEffect(() => {
    fetchCounts();
  }, []);

  const fetchCounts = async () => {
    try {
      const driversRes = await API.get("/drivers");
      const stopsRes = await API.get("/stops/school");

      setCounts({
        drivers: driversRes.data.length,
        stops: stopsRes.data.length,
      });
    } catch (err) {
      console.error("Error fetching school admin counts:", err);
    }
  };

  return (
    <div className="p-6">
      <h1 className="text-2xl font-bold mb-6">School Admin Dashboard</h1>

      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        <div className="bg-blue-500 text-white p-6 rounded-lg shadow text-center">
          <h2 className="text-lg font-semibold">Drivers</h2>
          <p className="text-3xl font-bold">{counts.drivers}</p>
        </div>
        <div className="bg-green-500 text-white p-6 rounded-lg shadow text-center">
          <h2 className="text-lg font-semibold">Stops</h2>
          <p className="text-3xl font-bold">{counts.stops}</p>
        </div>
      </div>
    </div>
  );
}

export default SchoolDashboard;
