// src/pages/ManageDrivers.js
import { useEffect, useState } from "react";
import axios from "axios";

export default function ManageDrivers() {
  const [drivers, setDrivers] = useState([]);
  const [form, setForm] = useState({ name: "", username: "", password: "" });
  const [editId, setEditId] = useState(null);
  const role = localStorage.getItem("role");
  const token = localStorage.getItem("token");

  const headers = { Authorization: `Bearer ${token}` };

  useEffect(() => {
    if (role === "schooladmin") {
      fetchDrivers();
    }
  }, []);

  // ✅ Fetch drivers (schooladmin only)
  const fetchDrivers = async () => {
    try {
      const res = await axios.get("https://gps.smartbus360.com/api/drivers", { headers });
      setDrivers(res.data);
    } catch (err) {
      console.error("Error fetching drivers:", err);
    }
  };

  // ✅ Add or Update driver
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (editId) {
        await axios.put(
          `https://gps.smartbus360.com/api/drivers/${editId}`,
          { name: form.name, password: form.password },
          { headers }
        );
      } else {
        await axios.post("https://gps.smartbus360.com/api/drivers", form, { headers });
      }
      setForm({ name: "", username: "", password: "" });
      setEditId(null);
      fetchDrivers();
    } catch (err) {
      console.error("Error saving driver:", err);
    }
  };

  // ✅ Delete driver
  const handleDelete = async (id) => {
    if (!window.confirm("Are you sure you want to delete this driver?")) return;
    try {
      await axios.delete(`https://gps.smartbus360.com/api/drivers/${id}`, { headers });
      fetchDrivers();
    } catch (err) {
      console.error("Error deleting driver:", err);
    }
  };

  // ✅ Fill form for editing
  const handleEdit = (driver) => {
    setForm({ name: driver.name, username: driver.username, password: "" });
    setEditId(driver.id);
  };

  if (role === "superadmin") {
    return (
      <div>
        <h1 className="text-2xl font-bold mb-6">🚌 Manage Drivers</h1>
        <p className="text-red-600">⚠️ Superadmin cannot directly manage drivers. Please log in as a school admin.</p>
      </div>
    );
  }

  return (
    <div>
      <h1 className="text-2xl font-bold mb-6">🚌 Manage Drivers</h1>

      {/* ✅ Add/Edit Form (schooladmin only) */}
      <form onSubmit={handleSubmit} className="mb-6 bg-gray-100 p-4 rounded">
        <h2 className="text-lg font-semibold mb-4">
          {editId ? "✏️ Edit Driver" : "➕ Add Driver"}
        </h2>
        <input
          type="text"
          placeholder="Driver Name"
          value={form.name}
          onChange={(e) => setForm({ ...form, name: e.target.value })}
          required
          className="border px-3 py-2 mr-2 rounded"
        />
        {!editId && (
          <input
            type="text"
            placeholder="Username"
            value={form.username}
            onChange={(e) => setForm({ ...form, username: e.target.value })}
            required
            className="border px-3 py-2 mr-2 rounded"
          />
        )}
        <input
          type="password"
          placeholder={editId ? "New Password (optional)" : "Password"}
          value={form.password}
          onChange={(e) => setForm({ ...form, password: e.target.value })}
          className="border px-3 py-2 mr-2 rounded"
        />
        <button
          type="submit"
          className="bg-blue-600 text-white px-4 py-2 rounded"
        >
          {editId ? "Update" : "Add"}
        </button>
        {editId && (
          <button
            type="button"
            onClick={() => {
              setForm({ name: "", username: "", password: "" });
              setEditId(null);
            }}
            className="ml-2 bg-gray-500 text-white px-4 py-2 rounded"
          >
            Cancel
          </button>
        )}
      </form>

      {/* ✅ Drivers Table */}
      {drivers.length === 0 ? (
        <p>No drivers found.</p>
      ) : (
        <table className="min-w-full bg-white border rounded shadow">
          <thead>
            <tr className="bg-gray-200 text-left">
              <th className="py-2 px-4 border">ID</th>
              <th className="py-2 px-4 border">Name</th>
              <th className="py-2 px-4 border">Username</th>
              <th className="py-2 px-4 border">Role</th>
              <th className="py-2 px-4 border">Created At</th>
              <th className="py-2 px-4 border">Actions</th>
            </tr>
          </thead>
          <tbody>
            {drivers.map((driver) => (
              <tr key={driver.id}>
                <td className="py-2 px-4 border">{driver.id}</td>
                <td className="py-2 px-4 border">{driver.name}</td>
                <td className="py-2 px-4 border">{driver.username}</td>
                <td className="py-2 px-4 border">{driver.role}</td>
                <td className="py-2 px-4 border">{new Date(driver.created_at).toLocaleDateString()}</td>
                <td className="py-2 px-4 border">
                  <button
                    onClick={() => handleEdit(driver)}
                    className="bg-yellow-500 text-white px-3 py-1 rounded mr-2"
                  >
                    Edit
                  </button>
                  <button
                    onClick={() => handleDelete(driver.id)}
                    className="bg-red-600 text-white px-3 py-1 rounded"
                  >
                    Delete
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}
