import { NavLink, useNavigate } from "react-router-dom";
import "./Sidebar.css";

export default function Sidebar({ role }) {
  const navigate = useNavigate();

  const handleLogout = () => {
    localStorage.clear();
    navigate("/login");
  };

  return (
    <div className="sidebar-container">
      <div>
        <h2 className="sidebar-title">Admin Panel</h2>
        <ul className="sidebar-menu">
          <li>
            <NavLink
              to="/dashboard"
              className={({ isActive }) =>
                isActive ? "sidebar-link active" : "sidebar-link"
              }
            >
              Dashboard
            </NavLink>
          </li>

          {role === "superadmin" && (
            <>
              <li>
                <NavLink
                  to="/schools"
                  className={({ isActive }) =>
                    isActive ? "sidebar-link active" : "sidebar-link"
                  }
                >
                  Manage Schools
                </NavLink>
              </li>
              <li>
                <NavLink
                  to="/admins"
                  className={({ isActive }) =>
                    isActive ? "sidebar-link active" : "sidebar-link"
                  }
                >
                  Manage Admins
                </NavLink>
              </li>
              <li>
                <NavLink
                  to="/drivers"
                  className={({ isActive }) =>
                    isActive ? "sidebar-link active" : "sidebar-link"
                  }
                >
                  Manage Drivers
                </NavLink>
              </li>
              <li>
                <NavLink
                  to="/stops"
                  className={({ isActive }) =>
                    isActive ? "sidebar-link active" : "sidebar-link"
                  }
                >
                  Manage Stops
                </NavLink>
              </li>
            </>
          )}

          {role === "schooladmin" && (
            <>
              <li>
                <NavLink
                  to="/schools"
                  className={({ isActive }) =>
                    isActive ? "sidebar-link active" : "sidebar-link"
                  }
                >
                  My School
                </NavLink>
              </li>
              <li>
                <NavLink
                  to="/drivers"
                  className={({ isActive }) =>
                    isActive ? "sidebar-link active" : "sidebar-link"
                  }
                >
                  Manage Drivers
                </NavLink>
              </li>
              <li>
                <NavLink
                  to="/stops"
                  className={({ isActive }) =>
                    isActive ? "sidebar-link active" : "sidebar-link"
                  }
                >
                  Manage Stops
                </NavLink>
              </li>
            </>
          )}
        </ul>
      </div>

      {role && (
        <button className="logout-btn" onClick={handleLogout}>
          Logout
        </button>
      )}
    </div>
  );
}
