// src/pages/SchoolAdmin/Drivers.jsx
import { useEffect, useState } from "react";
import API from "../../services/api";

function Drivers() {
  const [drivers, setDrivers] = useState([]);
  const [form, setForm] = useState({ name: "", username: "", password: "" });
  const [editId, setEditId] = useState(null);

  useEffect(() => {
    fetchDrivers();
  }, []);

  const fetchDrivers = async () => {
    try {
      const res = await API.get("/drivers");
      setDrivers(res.data);
    } catch (err) {
      console.error("Error fetching drivers:", err);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (editId) {
        await API.put(`/drivers/${editId}`, form);
      } else {
        await API.post("/drivers", form);
      }
      setForm({ name: "", username: "", password: "" });
      setEditId(null);
      fetchDrivers();
    } catch (err) {
      console.error("Error saving driver:", err);
    }
  };

  const deleteDriver = async (id) => {
    if (!window.confirm("Are you sure you want to delete this driver?")) return;
    try {
      await API.delete(`/drivers/${id}`);
      fetchDrivers();
    } catch (err) {
      console.error("Error deleting driver:", err);
    }
  };

  const startEdit = (d) => {
    setEditId(d.id);
    setForm({ name: d.name, username: d.username, password: "" });
  };

  return (
    <div className="p-6">
      <h1 className="text-2xl font-bold mb-6">Manage Drivers</h1>

      {/* Add/Edit Form */}
      <form onSubmit={handleSubmit} className="mb-6 bg-white p-4 rounded shadow flex gap-3">
        <input
          type="text"
          placeholder="Driver Name"
          value={form.name}
          onChange={(e) => setForm({ ...form, name: e.target.value })}
          className="p-2 border rounded flex-1"
          required
        />
        <input
          type="text"
          placeholder="Username"
          value={form.username}
          onChange={(e) => setForm({ ...form, username: e.target.value })}
          className="p-2 border rounded flex-1"
          required
        />
        <input
          type="password"
          placeholder="Password"
          value={form.password}
          onChange={(e) => setForm({ ...form, password: e.target.value })}
          className="p-2 border rounded flex-1"
          required={!editId}
        />
        <button type="submit" className="bg-blue-500 text-white px-4 rounded">
          {editId ? "Update" : "Add"}
        </button>
        {editId && (
          <button
            type="button"
            onClick={() => {
              setEditId(null);
              setForm({ name: "", username: "", password: "" });
            }}
            className="bg-gray-400 text-white px-4 rounded"
          >
            Cancel
          </button>
        )}
      </form>

      {/* Drivers Table */}
      <div className="bg-white p-4 rounded shadow">
        <table className="w-full border">
          <thead>
            <tr className="bg-gray-200">
              <th className="p-2 border">ID</th>
              <th className="p-2 border">Name</th>
              <th className="p-2 border">Username</th>
              <th className="p-2 border">Role</th>
              <th className="p-2 border">Actions</th>
            </tr>
          </thead>
          <tbody>
            {drivers.map((d) => (
              <tr key={d.id}>
                <td className="p-2 border">{d.id}</td>
                <td className="p-2 border">{d.name}</td>
                <td className="p-2 border">{d.username}</td>
                <td className="p-2 border">{d.role}</td>
                <td className="p-2 border flex gap-2">
                  <button
                    onClick={() => startEdit(d)}
                    className="bg-yellow-500 text-white px-2 py-1 rounded"
                  >
                    Edit
                  </button>
                  <button
                    onClick={() => deleteDriver(d.id)}
                    className="bg-red-500 text-white px-2 py-1 rounded"
                  >
                    Delete
                  </button>
                </td>
              </tr>
            ))}
            {drivers.length === 0 && (
              <tr>
                <td colSpan="5" className="text-center p-3">
                  No drivers found
                </td>
              </tr>
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default Drivers;
