// src/pages/SuperAdmin/Dashboard.jsx
import { useEffect, useState } from "react";
import API from "../../services/api";

function Dashboard() {
  const [counts, setCounts] = useState({
    schools: 0,
    admins: 0,
    drivers: 0,
    stops: 0,
  });

  useEffect(() => {
    fetchCounts();
  }, []);

  const fetchCounts = async () => {
    try {
      // Fetch schools
      const schoolsRes = await API.get("/schools");
      // Fetch admins
      const adminsRes = await API.get("/schooladmins");
      // Fetch stops
      const stopsRes = await API.get("/stops/all");

      setCounts({
        schools: schoolsRes.data.length,
        admins: adminsRes.data.length,
        drivers: 0, // ⚠️ Needs backend endpoint for all drivers
        stops: stopsRes.data.length,
      });
    } catch (err) {
      console.error("Error fetching counts:", err);
    }
  };

  return (
    <div className="p-6">
      <h1 className="text-2xl font-bold mb-6">Superadmin Dashboard</h1>

      <div className="grid grid-cols-1 md:grid-cols-4 gap-6">
        <div className="bg-blue-500 text-white p-6 rounded-lg shadow text-center">
          <h2 className="text-lg font-semibold">Schools</h2>
          <p className="text-3xl font-bold">{counts.schools}</p>
        </div>
        <div className="bg-green-500 text-white p-6 rounded-lg shadow text-center">
          <h2 className="text-lg font-semibold">School Admins</h2>
          <p className="text-3xl font-bold">{counts.admins}</p>
        </div>
        <div className="bg-yellow-500 text-white p-6 rounded-lg shadow text-center">
          <h2 className="text-lg font-semibold">Drivers</h2>
          <p className="text-3xl font-bold">{counts.drivers}</p>
          <p className="text-xs">(needs backend API for all drivers)</p>
        </div>
        <div className="bg-red-500 text-white p-6 rounded-lg shadow text-center">
          <h2 className="text-lg font-semibold">Stops</h2>
          <p className="text-3xl font-bold">{counts.stops}</p>
        </div>
      </div>
    </div>
  );
}

export default Dashboard;
