import { useEffect, useState } from "react";
import API from "../../services/api";

function ManageDrivers() {
  const [drivers, setDrivers] = useState([]);

  useEffect(() => {
    fetchDrivers();
  }, []);

  const fetchDrivers = async () => {
    try {
      const res = await API.get("/drivers/all");
      setDrivers(res.data);
    } catch (err) {
      console.error("Error fetching drivers:", err);
    }
  };

  return (
    <div className="p-6">
      <h1 className="text-2xl font-bold mb-6">Manage Drivers (All Schools)</h1>

      <div className="bg-white p-4 rounded shadow">
        <table className="w-full border text-sm">
          <thead>
            <tr className="bg-gray-200">
              <th className="p-2 border">ID</th>
              <th className="p-2 border">Name</th>
              <th className="p-2 border">Username</th>
              <th className="p-2 border">Role</th>
              <th className="p-2 border">School</th>
              <th className="p-2 border">Created At</th>
            </tr>
          </thead>
          <tbody>
            {drivers.map((d) => (
              <tr key={d.id}>
                <td className="p-2 border">{d.id}</td>
                <td className="p-2 border">{d.name}</td>
                <td className="p-2 border">{d.username}</td>
                <td className="p-2 border">{d.role}</td>
                <td className="p-2 border">{d.school_name}</td>
                <td className="p-2 border">
                  {new Date(d.created_at).toLocaleString()}
                </td>
              </tr>
            ))}
            {drivers.length === 0 && (
              <tr>
                <td colSpan="6" className="text-center p-3">
                  No drivers found
                </td>
              </tr>
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default ManageDrivers;
