import { useEffect, useState } from "react";
import axios from "axios";

export default function ManageDrivers() {
  const [drivers, setDrivers] = useState([]);
  const [selectedDriver, setSelectedDriver] = useState(null);
  const [stops, setStops] = useState([]);
  const role = localStorage.getItem("role");
  const token = localStorage.getItem("token");

  const headers = { Authorization: `Bearer ${token}` };

  useEffect(() => {
    if (role === "schooladmin") {
      fetchDrivers();
    }
  }, []);

  // ‚úÖ Fetch all drivers for school
  const fetchDrivers = async () => {
    try {
      const res = await axios.get("https://gps.smartbus360.com/api/drivers", { headers });
      setDrivers(res.data);
    } catch (err) {
      console.error("Error fetching drivers:", err);
    }
  };

  // ‚úÖ Fetch stops for a specific driver
  const fetchStopsForDriver = async (driverId) => {
    try {
      const res = await axios.get("https://gps.smartbus360.com/api/stops/school", { headers });
      // filter client-side for now
      const driverStops = res.data.filter((s) => s.driver_name && s.driver_id === driverId);
      setStops(driverStops);
    } catch (err) {
      console.error("Error fetching stops:", err);
    }
  };

  // ‚úÖ Handle driver selection
  const handleSelectDriver = (driver) => {
    setSelectedDriver(driver);
    fetchStopsForDriver(driver.id);
  };

  if (role === "superadmin") {
    return (
      <div>
        <h1 className="text-2xl font-bold mb-6">üöå Manage Drivers</h1>
        <p className="text-red-600">
          ‚ö†Ô∏è Superadmin cannot directly manage drivers. Please log in as a school admin.
        </p>
      </div>
    );
  }

  return (
    <div>
      <h1 className="text-2xl font-bold mb-6">üöå Manage Drivers</h1>

      {!selectedDriver ? (
        // ‚úÖ Step 1: Show driver list
        <ul className="space-y-4">
          {drivers.map((driver) => (
            <li
              key={driver.id}
              className="p-4 bg-gray-100 rounded shadow cursor-pointer hover:bg-gray-200"
              onClick={() => handleSelectDriver(driver)}
            >
              <h2 className="text-lg font-semibold">{driver.name}</h2>
              <p className="text-sm text-gray-600">Username: {driver.username}</p>
            </li>
          ))}
        </ul>
      ) : (
        // ‚úÖ Step 2: Show selected driver details + stops
        <div>
          <button
            onClick={() => setSelectedDriver(null)}
            className="mb-4 bg-gray-500 text-white px-4 py-2 rounded"
          >
            ‚Üê Back to Drivers
          </button>

          <h2 className="text-xl font-bold mb-2">
            Driver: {selectedDriver.name} ({selectedDriver.username})
          </h2>

          <h3 className="text-lg font-semibold mt-4 mb-2">Stops Recorded:</h3>
          {stops.length === 0 ? (
            <p>No stops found for this driver.</p>
          ) : (
            <table className="min-w-full bg-white border rounded shadow">
              <thead>
                <tr className="bg-gray-200 text-left">
                  <th className="py-2 px-4 border">Round</th>
                  <th className="py-2 px-4 border">Stop #</th>
                  <th className="py-2 px-4 border">Place</th>
                  <th className="py-2 px-4 border">Lat</th>
                  <th className="py-2 px-4 border">Long</th>
                </tr>
              </thead>
              <tbody>
                {stops.map((stop) => (
                  <tr key={stop.id}>
                    <td className="py-2 px-4 border">{stop.round_name}</td>
                    <td className="py-2 px-4 border">{stop.stop_order}</td>
                    <td className="py-2 px-4 border">{stop.placename}</td>
                    <td className="py-2 px-4 border">{stop.latitude}</td>
                    <td className="py-2 px-4 border">{stop.longitude}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          )}
        </div>
      )}
    </div>
  );
}
