// src/pages/SchoolAdmin/Stops.jsx
import { useEffect, useState } from "react";
import API from "../../services/api";

function Stops() {
  const [stops, setStops] = useState([]);

  useEffect(() => {
    fetchStops();
  }, []);

  const fetchStops = async () => {
    try {
      const res = await API.get("/stops/school");
      setStops(res.data);
    } catch (err) {
      console.error("Error fetching stops:", err);
    }
  };

  const exportCSV = () => {
    window.open("http://localhost:5001/api/stops/export/school", "_blank");
  };

  return (
    <div className="p-6">
      <h1 className="text-2xl font-bold mb-6">School Stops</h1>

      <div className="flex justify-end mb-4">
        <button onClick={exportCSV} className="bg-green-500 text-white px-4 py-2 rounded">
          Export CSV
        </button>
      </div>

      <div className="bg-white p-4 rounded shadow">
        <table className="w-full border text-sm">
          <thead>
            <tr className="bg-gray-200">
              <th className="p-2 border">Round</th>
              <th className="p-2 border">Order</th>
              <th className="p-2 border">Latitude</th>
              <th className="p-2 border">Longitude</th>
              <th className="p-2 border">Placename</th>
              <th className="p-2 border">Driver</th>
              <th className="p-2 border">Created At</th>
            </tr>
          </thead>
          <tbody>
            {stops.map((st, idx) => (
              <tr key={idx}>
                <td className="p-2 border">{st.round_name}</td>
                <td className="p-2 border">{st.stop_order}</td>
                <td className="p-2 border">{st.latitude}</td>
                <td className="p-2 border">{st.longitude}</td>
                <td className="p-2 border">{st.placename}</td>
                <td className="p-2 border">{st.driver_name}</td>
                <td className="p-2 border">{new Date(st.created_at).toLocaleString()}</td>
              </tr>
            ))}
            {stops.length === 0 && (
              <tr>
                <td colSpan="7" className="text-center p-3">
                  No stops found
                </td>
              </tr>
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default Stops;
